  transition: all 0.3s ease-in-out;
  padding: 0.2em 0;
  &:hover {
   border-bottom: 2px solid $primaryColor;
   opacity: 0.9;
  }
 }

 &-copyright {
  text-align: center;
  padding: 1em;
 }
}

